#[cfg(test)]
struct TestData {
    signal_1byte: Vec<Complex<f32>>,
    signal_2bytes: Vec<Complex<f32>>,
    signal_4bytes: Vec<Complex<f32>>,
    signal_8bytes: Vec<Complex<f32>>,
    signal_16bytes: Vec<Complex<f32>>,
    signal_32bytes: Vec<Complex<f32>>,
    signal_64bytes: Vec<Complex<f32>>,
    signal_128bytes: Vec<Complex<f32>>,
    signal_256bytes: Vec<Complex<f32>>,
    signal_512bytes: Vec<Complex<f32>>,
    signal_1024bytes: Vec<Complex<f32>>,
    signal_2048bytes: Vec<Complex<f32>>,
    instance: Demodulators,
}

#[cfg(test)]
impl Default for TestData {
    fn default() -> Self {
        let demods = Demodulators::new((SAMPLE_RATE / BAUD_RATE) as usize, SAMPLE_RATE);
        let mods = Modulators::new((SAMPLE_RATE / BAUD_RATE) as usize, SAMPLE_RATE);

        TestData {
            signal_1byte: mods.bpsk(BYTE_1),
            signal_2bytes: mods.bpsk(BYTES_2),
            signal_4bytes: mods.bpsk(BYTES_4),
            signal_8bytes: mods.bpsk(BYTES_8),
            signal_16bytes: mods.bpsk(BYTES_16),
            signal_32bytes: mods.bpsk(BYTES_32),
            signal_64bytes: mods.bpsk(BYTES_64),
            signal_128bytes: mods.bpsk(BYTES_128),
            signal_256bytes: mods.bpsk(BYTES_256),
            signal_512bytes: mods.bpsk(BYTES_512),
            signal_1024bytes: mods.bpsk(BYTES_1024),
            signal_2048bytes: mods.bpsk(BYTES_2048),
            instance: demods,
        }
    }
}

lazy_static! {
    static ref DATA:TestData = TestData::default();
}


#[test]
pub fn bpsk_byte_1() {
    let test = DATA.instance.bpsk(DATA.signal_1byte.clone());

    let expected = BYTE_1;


    assert_eq!(
        test,
        expected,
        "Testing bpsk With 1 Byte of Data.\
            Expected: {:?}\
            Got: {:?}",
        expected,
        test
    )
}


